package day4;

public class Array_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[] = {3,4,5,1,2};
		for(int i=0; i<arr.length; i++) {
			for(int j=0; j<arr[i]; j++) {
				System.out.print("*");
			}
			System.out.println();
		}
		
		
	}

}
