package javaIO;

public class Print_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "홍길동";
		int age = 30;
		// 홍길동의 나이는 30 입니다.
		System.out.println(name + "의 나이는 " + age + " 입니다.");
		System.out.printf("%s의 나이는 %d 입니다.", name, age);
		// 소수점 %f, bool %b
		
		
		
	}

}
